import React, { useState } from 'react';
import { geminiApi } from '../services/geminiApi';

const HomePage = () => {
  const [csvData, setCsvData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [fileName, setFileName] = useState('');
  const [aiSearchQuery, setAiSearchQuery] = useState('');

  const parseCSV = (text) => {
    const lines = text.split('\n');
    const parsedData = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();
      if (line) {
        // Simple CSV parsing - handles basic cases
        const values = line.split(',').map(value => value.trim().replace(/^"|"$/g, ''));
        parsedData.push(values);
      }
    }

    return parsedData;
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'text/csv') {
      setFileName(file.name);
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target.result;
        const parsedData = parseCSV(text);

        if (parsedData.length > 0) {
          setHeaders(parsedData[0]);
          setCsvData(parsedData.slice(1));
        }
      };
      reader.readAsText(file);
    }
  };

  const handleFileUpload = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.csv';
    input.onchange = handleFileSelect;
    input.click();
  };

  const handleDummyAction = (action) => {
    alert(`${action} functionality will be implemented soon!`);
  };

  const handleAiSearch = () => {
    if (aiSearchQuery.trim()) {
      alert(`AI Search: "${aiSearchQuery}" - This will analyze your CSV data and provide insights!`);
    } else {
      alert('Please enter a search query first!');
    }
  };

  return (
    <div className="content-card">
      <h2>Welcome to Zenarth</h2>
      <p>Your modern dashboard for managing everything in one place.</p>
      <button 
        className="upload-button"
        onClick={handleFileUpload}
        style={{
          marginTop: '20px',
          padding: '12px 24px',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontSize: '16px',
          fontWeight: '600',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          transition: 'transform 0.2s, box-shadow 0.2s'
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = 'translateY(-2px)';
          e.currentTarget.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.4)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = 'translateY(0)';
          e.currentTarget.style.boxShadow = 'none';
        }}
      >
        <i className="fas fa-file-csv"></i>
        Upload CSV
      </button>

      {csvData.length > 0 && (
        <>
          {/* AI Search Section */}
          <div style={{ 
            marginTop: '30px', 
            padding: '20px', 
            backgroundColor: '#f8f9fa', 
            borderRadius: '12px',
            border: '1px solid #e9ecef'
          }}>
            <h3 style={{ marginBottom: '15px', color: '#333', display: 'flex', alignItems: 'center', gap: '8px' }}>
              <i className="fas fa-robot" style={{ color: '#667eea' }}></i>
              AI Data Assistant
            </h3>
            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <input
                type="text"
                placeholder="Find me the best one"
                value={aiSearchQuery}
                onChange={(e) => setAiSearchQuery(e.target.value)}
                style={{
                  flex: 1,
                  padding: '12px 16px',
                  border: '2px solid #dee2e6',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = '#667eea';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = '#dee2e6';
                }}
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    handleAiSearch();
                  }
                }}
              />
              <button
                onClick={handleAiSearch}
                style={{
                  padding: '12px 20px',
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '14px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  transition: 'transform 0.2s, box-shadow 0.2s'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-2px)';
                  e.currentTarget.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.4)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = 'none';
                }}
              >
                <i className="fas fa-search"></i>
                Search
              </button>
            </div>
          </div>

          {/* Action Buttons Section */}
          <div style={{ marginTop: '20px' }}>
            <h3 style={{ marginBottom: '15px', color: '#333' }}>Quick Actions</h3>
            <div style={{
              display: 'flex',
              gap: '12px',
              overflowX: 'auto',
              paddingBottom: '10px',
              scrollbarWidth: 'thin',
              scrollbarColor: '#667eea #f1f1f1'
            }}>
              {[
                'Show Top 10',
                'Filter by Date',
                'Sort A-Z',
                'Sort Z-A',
                'Export Data',
                'Generate Report',
                'Find Duplicates',
                'Remove Empty Rows',
                'Calculate Sum',
                'Find Max Value',
                'Find Min Value',
                'Group by Category'
              ].map((action, index) => (
                <button
                  key={index}
                  onClick={() => handleDummyAction(action)}
                  style={{
                    padding: '10px 16px',
                    backgroundColor: 'white',
                    color: '#667eea',
                    border: '2px solid #667eea',
                    borderRadius: '20px',
                    fontSize: '13px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    whiteSpace: 'nowrap',
                    transition: 'all 0.2s',
                    minWidth: 'fit-content'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#667eea';
                    e.currentTarget.style.color = 'white';
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.boxShadow = '0 4px 12px rgba(102, 126, 234, 0.3)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'white';
                    e.currentTarget.style.color = '#667eea';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = 'none';
                  }}
                >
                  {action}
                </button>
              ))}
            </div>
          </div>
        </>
      )}

      {csvData.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3 style={{ marginBottom: '15px', color: '#333' }}>
            CSV Data: {fileName}
          </h3>
          <div style={{ 
            overflowX: 'auto', 
            border: '1px solid #ddd', 
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
          }}>
            <table style={{
              width: '100%',
              borderCollapse: 'collapse',
              backgroundColor: 'white'
            }}>
              <thead>
                <tr style={{ backgroundColor: '#f8f9fa' }}>
                  {headers.map((header, index) => (
                    <th key={index} style={{
                      padding: '12px 16px',
                      textAlign: 'left',
                      fontWeight: '600',
                      color: '#333',
                      borderBottom: '2px solid #dee2e6',
                      fontSize: '14px'
                    }}>
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {csvData.map((row, rowIndex) => (
                  <tr key={rowIndex} style={{
                    borderBottom: '1px solid #dee2e6',
                    transition: 'background-color 0.2s'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#f8f9fa';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'white';
                  }}>
                    {row.map((cell, cellIndex) => (
                      <td key={cellIndex} style={{
                        padding: '12px 16px',
                        fontSize: '14px',
                        color: '#555',
                        maxWidth: '200px',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        whiteSpace: 'nowrap'
                      }}>
                        {cell}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <p style={{ 
            marginTop: '10px', 
            fontSize: '12px', 
            color: '#666',
            fontStyle: 'italic'
          }}>
            Showing {csvData.length} rows with {headers.length} columns
          </p>
        </div>
      )}
    </div>
  );
};

export default HomePage;
