import { useState } from 'react';
import './App.css';
import logo from './zenarth.png';
import HomePage from './pages/HomePage';
import CompanyPage from './pages/CompanyPage';
import FriendsPage from './pages/FriendsPage';
import PrivacyPage from './pages/PrivacyPage';
import AccountPage from './pages/AccountPage';
import DefaultPage from './pages/DefaultPage';

function App() 
{
  const [activeSection, setActiveSection] = useState('home');
  const [activeSubSection, setActiveSubSection] = useState(null);

  const sections = 
  {
    home: { icon: 'fa-house', subSections: [] },
    people: 
    { 
      icon: 'fa-users', 
      subSections: ['Company', 'Friends'] 
    },
    settings: 
    { 
      icon: 'fa-gear', 
      subSections: ['Privacy', 'Account'] 
    }
  };

  const getPageTitle = () => 
  {
    if (activeSubSection) 
    {
      return activeSubSection;
    }
    return activeSection.charAt(0).toUpperCase() + activeSection.slice(1);
  };

  const getPageContent = () => 
  {
      const handleFileSelect = () => 
    {
      const input = document.createElement('input');
      input.type = 'file';
      input.accept = '.csv';
      input.click();
    };

    if (activeSection === 'home') 
    {
      return (
        <div className="content-card">
          <h2>Welcome to Zenarth</h2>
          <p>Your modern dashboard for managing everything in one place.</p>
          <button 
            className="upload-button"
            onClick={handleFileSelect}
            style=
            {{
              marginTop: '20px',
              padding: '12px 24px',
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              transition: 'transform 0.2s, box-shadow 0.2s'
            }}
            onMouseEnter={(e) => 
            {
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.4)';
            }}
            onMouseLeave={(e) => 
            {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = 'none';
            }}
          >
            <i className="fas fa-file-csv"></i>
            Upload CSV
          </button>
        </div>
      );
    }

    if (activeSection === 'people') 
    {
      if (activeSubSection === 'Company') 
      {
        return (
          <div className="content-card">
            <h2>Company Contacts</h2>
            <div className="contact-list">
              <div className="contact-item">
                <i className="fas fa-user-tie"></i>
                <span>John Anderson - CEO</span>
              </div>
              <div className="contact-item">
                <i className="fas fa-user-tie"></i>
                <span>Sarah Mitchell - CTO</span>
              </div>
              <div className="contact-item">
                <i className="fas fa-user-tie"></i>
                <span>Michael Chen - Designer</span>
              </div>
            </div>
          </div>
        );
      }

      if (activeSubSection === 'Friends') 
      {
        return (
          <div className="content-card">
            <h2>Friends</h2>
            <div className="contact-list">
              <div className="contact-item">
                <i className="fas fa-user"></i>
                <span>Emma Wilson</span>
              </div>
              <div className="contact-item">
                <i className="fas fa-user"></i>
                <span>David Park</span>
              </div>
              <div className="contact-item">
                <i className="fas fa-user"></i>
                <span>Lisa Rodriguez</span>
              </div>
            </div>
          </div>
        );
      }
    }

    if (activeSection === 'settings') 
    {
      if (activeSubSection === 'Privacy') 
      {
        return (
          <div className="content-card">
            <h2>Privacy Settings</h2>
            <div className="settings-option">
              <span>Profile Visibility</span>
              <button className="toggle-btn">Public</button>
            </div>
            <div className="settings-option">
              <span>Data Sharing</span>
              <button className="toggle-btn">Enabled</button>
            </div>
            <div className="settings-option">
              <span>Activity Status</span>
              <button className="toggle-btn">Visible</button>
            </div>
          </div>
        );
      }

      if (activeSubSection === 'Account') 
      {
        return (
          <div className="content-card">
            <h2>Account Settings</h2>
            <div className="settings-option">
              <span>Email</span>
              <span className="setting-value">user@zenarth.com</span>
            </div>
            <div className="settings-option">
              <span>Password</span>
              <button className="change-btn">Change</button>
            </div>
            <div className="settings-option">
              <span>Two-Factor Auth</span>
              <button className="toggle-btn">Enabled</button>
            </div>
          </div>
        );
      }
    }

    return (
      <div className="content-card">
        <h2>{getPageTitle()}</h2>
        <p>Select a section from the sidebar to view details.</p>
      </div>
    );
  };

  return (
    <>
      <link 
        rel="stylesheet" 
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css" 
      />

      <div className="dashboard">
        <div className="sidebar-main">
          <div className="company-name">
            <img src={logo} alt="Zenarth" />
            <span>Zenarth</span>
          </div>

          <nav className="nav-menu">
            {Object.keys(sections).map(section => (
              <button
                key={section}
                className={`nav-item ${activeSection === section ? 'active' : ''}`}
                onClick={() => 
                {
                  setActiveSection(section);
                  setActiveSubSection(
                    sections[section].subSections.length > 0 
                      ? sections[section].subSections[0] 
                      : null
                  );
                }}
              >
                <i className={`fas ${sections[section].icon}`}></i>
                <span>{section.charAt(0).toUpperCase() + section.slice(1)}</span>
              </button>
            ))}
          </nav>
        </div>

        {sections[activeSection].subSections.length > 0 && (
          <div className="sidebar-sub">
            <div className="sub-title">
              {activeSection.charAt(0).toUpperCase() + activeSection.slice(1)}
            </div>

            {sections[activeSection].subSections.map(subSection => (
              <button
                key={subSection}
                className={`sub-item ${activeSubSection === subSection ? 'active' : ''}`}
                onClick={() => setActiveSubSection(subSection)}
              >
                {subSection}
              </button>
            ))}
          </div>
        )}

        <div className="main-content">
          <div className="navbar">
            <h1 className="page-title">{getPageTitle()}</h1>
            <button className="help-button">
              <i className="fas fa-question"></i>
            </button>
          </div>

          <div className="page-section">
            {getPageContent()}
          </div>
        </div>
      </div>

      <style jsx>{`
      `}</style>
    </>
  );
}

export default App;
